<script>
  import './App.css';
  import {CKEditor, InlineEditor, BalloonEditor, AutosavePlugin, MediaEmbedPlugin, UploaderPlugin} from './index';
  export default {
      data() {
          return {
            content: '',
            inlineEditor: InlineEditor,
            editorConfig: {
                toolbar: {
                    viewportTopOffset : 50,    //<-- height of fixed header
                    items: [
                        "heading", "|", "fontsize", "fontfamily", "|", "bold", "italic", "underline", "strikethrough", "highlight", "|", "alignment", "|", 
                        "numberedList", "bulletedList", "|", "link", "blockquote", "imageUpload", "insertTable", "mediaEmbed", "|", "undo", "redo",
                    ],
                },
                allowedContent: true,
                // extraAllowedContent: '*(*);*{*}',
                extraPlugins: null,
                // extraPlugins = [UploaderPlugin, Autosave, AffBox,CKMedia(this.mediaDialog)];
                autosave: {
                    waitingTime: 60000,
                },
                mediaEmbed: MediaEmbedPlugin
            } 
          }
      },
	  mounted() {
	    InlineEditor
            .create( document.querySelector( '#editor' ) )
            .then( editor => {
                window.editor = editor;
            } )
            .catch( err => {
                console.error( err.stack );
            } );

        BalloonEditor
            .create( document.querySelector( '#balloon' ) )
            .catch( error => {
                console.error( error );
            } );
	  }
  }
</script>
